import React, { useState, useEffect } from 'react';

const Home = () => {
    const [tasks, setTasks] = useState([]);
    const [activity, setActivity] = useState('');
    const [blockchainStatus, setBlockchainStatus] = useState('');

    useEffect(() => {
        // Fetch tasks and blockchain status on component mount
        fetch('/tasks')
            .then(response => response.json())
            .then(data => setTasks(data))
            .catch(error => console.error('Error fetching tasks:', error));

        fetch('/isValid')
            .then(response => response.text())
            .then(data => setBlockchainStatus(data))
            .catch(error => console.error('Error checking blockchain validity:', error));
    }, []);

    const handleAddTask = () => {
        // Make a POST request to add a task
        fetch('/addTask', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ activity }),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // Refresh task list after adding a task
                fetch('/tasks')
                    .then(response => response.json())
                    .then(data => setTasks(data))
                    .catch(error => console.error('Error fetching tasks:', error));
            })
            .catch(error => console.error('Error adding task:', error));
    };

    const handleInsertMaliciousBlock = () => {
        // Make a POST request to insert a malicious block
        fetch('/insertMaliciousBlock', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ activity }),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // Refresh blockchain status after inserting a malicious block
                fetch('/isValid')
                    .then(response => response.text())
                    .then(data => setBlockchainStatus(data))
                    .catch(error => console.error('Error checking blockchain validity:', error));
            })
            .catch(error => console.error('Error inserting malicious block:', error));
    };

    return (
        <div className="container">
            <div style={{marginBottom: "35px", background: "#702963", width: "1260px", height: "70px", display: 'flex', alignItems: 'center', justifyContent: 'center'}}>


            <h2 style={{color: "white"}}> My Blockchain To-Do List</h2>
            </div>
            <div className="mb-4">
                <div className="input-group"style={{width: "520px"}}>
                    <input
                        type="text"
                        placeholder="Enter activity"
                        className="form-control"
                        value={activity}
                        onChange={e => setActivity(e.target.value)}
                    />
                    <div className="input-group-append">
                        <button style={{marginLeft: "25px", background: "#C3B1E13", border:'none', color: 'black'}} className="btn btn-primary" onClick={handleAddTask}>
                            Add New Activity
                        </button>
                    </div>
                </div>
            </div>

            <div className="mb-4" style={{width: "540px"}}>
                <div className="input-group">
                    <input
                        type="text"
                        placeholder="Enter Malicious Block"
                        className="form-control"
                        value={activity}
                        onChange={e => setActivity(e.target.value)}
                    />
                    <div className="input-group-append">
                        <button style={{marginLeft: "25px", background: '#953553', border: 'none'}}   className="btn btn-danger" onClick={handleInsertMaliciousBlock}>
                            Add Malicious Block
                        </button>
                    </div>
                </div>
            </div>

            <div>
                <h2 style={{fontSize: "25px"}}>To do: </h2>
                <ul className="list-group">
                    {tasks.map(task => (
                        <li key={task.id} className="list-group-item">
                            {task.name}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default Home;
