import React, { useState, useEffect } from 'react';

const Home = () => {
    const [tasks, setTasks] = useState([]);
    const [activity, setActivity] = useState('');
    const [maliciousBlock, setMaliciousBlock] = useState('');
    const [taskIdToUpdate, setTaskIdToUpdate] = useState('');

    useEffect(() => {
        // Fetch tasks and blockchain status on component mount
        fetch('http://localhost:8080/tasks')
            .then(response => response.json())
            .then(data => setTasks(data))
            .catch(error => console.error('Error fetching tasks:', error));
    }, []);

    const handleAddTask = () => {
        // Make a POST request to add a task
        fetch('http://localhost:8080/addTask', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ activity }),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // Clear the activity input after adding a task
                setActivity('');

                // Reload the entire page to fetch and display the updated list of tasks
                window.location.reload();
            })
            .catch(error => console.error('Error adding task:', error));
    };
    const handleUpdateTask = () => {
        fetch('http://localhost:8080/updateTask', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ taskId: taskIdToUpdate, activity }),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                setActivity('');
                setTaskIdToUpdate('');
                window.location.reload();
            })
            .catch(error => console.error('Error updating task:', error));
    };
    const handleEditTask = (taskId, currentActivity) => {
        setTaskIdToUpdate(taskId);
        setActivity(currentActivity);
    };
    const handleInsertMaliciousBlock = () => {
        // Make a POST request to insert a malicious block
        fetch('http://localhost:8080/insertMaliciousBlock', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ maliciousBlock }),
        })
            .then(response => response.json())
            .then(data => {
                console.log(data);
                // Clear the activity input after adding a task
                fetch('http://localhost:8080/isValid')
                    .then(response => response.text())
                    .then(validity => {
                        if (validity === 'Invalid Blockchain') {
                            // Display a success message if the blockchain is valid
                            alert('Malicious block inserted successfully! Blockchain is INVALID.');
                        }
                    })
            })
            .catch(error => console.error('Error inserting malicious block:', error));
    };

    return (
        <div className="container">
            <div style={{marginBottom: "35px", background: "#702963", width: "1290px", height: "70px", display: 'flex', alignItems: 'center', justifyContent: 'center'}}>


            <h2 style={{color: "white"}}> My Blockchain To-Do List</h2>
            </div>
            <div className="mb-4">
                <div className="input-group"style={{width: "520px"}}>
                    <input
                        type="text"
                        placeholder="Enter activity"
                        className="form-control"
                        value={activity}
                        onChange={e => setActivity(e.target.value)}
                    />
                    <div className="input-group-append">
                        <button style={{marginLeft: "25px", background: "#D8BFD8", border:'none', color: 'black'}} className="btn btn-primary" onClick={handleAddTask}>
                            Add New Activity
                        </button>
                    </div>
                    <div className="input-group-append">
                        <button
                            style={{ marginLeft: "25px", background: "#D8BFD8", border: 'none', color: 'black' }}
                            className="btn btn-primary"
                            onClick={taskIdToUpdate ? handleUpdateTask : handleAddTask}
                        >
                            {taskIdToUpdate ? 'Update Task' : 'Add New Activity'}
                        </button>
                    </div>

                </div>
            </div>

            <div className="mb-4" style={{width: "540px"}}>
                <div className="input-group">
                    <input
                        type="text"
                        placeholder="Enter Malicious Block"
                        className="form-control"
                        value={maliciousBlock}
                        onChange={e => setMaliciousBlock(e.target.value)}
                    />
                    <div className="input-group-append">
                        <button style={{marginLeft: "25px", background: '#953553', border: 'none'}}   className="btn btn-danger" onClick={handleInsertMaliciousBlock}>
                            Add Malicious Block
                        </button>
                    </div>
                </div>
            </div>

            <div>
                <table style={{ width: '110%', marginLeft: '-70px' }}>
                    <thead>
                    <tr>
                        <th style={{ width: '10%' }}>ID</th>
                        <th style={{ width: '20%' }}>Timestamp</th>
                        <th style={{ width: '30%' }}>Activity</th>
                        <th style={{ width: '50%' }}>Previous Task Hash</th>
                        <th style={{ width: '20%' }}>Task Hash</th>
                    </tr>
                    </thead>
                    <tbody>
                    {tasks.map(task => (
                        <tr key={task.id}>
                            <td>{task.taskId}</td>
                            <td>{task.timestamp}</td>
                            <td>{task.activity}</td>
                            <td>{task.previousTaskHash}</td>
                            <td>{task.taskHash}</td>
                            <td><button
                                style={{ background: "#D8BFD8", border: 'none', color: 'black' }}
                                className="btn btn-primary"
                                onClick={() => handleEditTask(task.taskId, task.activity)}
                            >
                                Edit
                            </button></td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default Home;
